import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return const MaterialApp(
      home: ListviewDemo(),
    );
  }
}

class ListviewDemo extends StatefulWidget {
  const ListviewDemo({super.key});

  @override
  State<ListviewDemo> createState() => _ListviewDemoState();
}

class _ListviewDemoState extends State<ListviewDemo> {
  List playerinfo = [
    [
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRKxJ9KhWH-LgjQpLK2WugonvSX3RA2gAgC2Q&usqp=CAU",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzXKczOsKLWCjVpiHZ2KBhb0OkvyXQZL82FQ&usqp=CAU",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT0GtrTRNqLjj31VbLOQsxdikcj6LJ_3EoC1w&usqp=CAU",
    ],
    [
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQxUO-GwokndX5xQg96jlTJl1x7grDsrCTjjQ&usqp=CAU",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJThKY-42WA38lrSRsLq-D6qUvzWzGylwZNw&usqp=CAU",
      "data:image/jpeg;base64,"
    ],
    [
      "data:image/jpeg;base64,",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbRq_LMkCoJ5YN_gUjq4pcOqkwGu562DzeWg&usqp=CAU",
      "https://bcciplayerimages.s3.ap-south-1.amazonaws.com/resizedimageskirti/1125_compress.png",
    ],
  ];

  List<String> cricformat = ["Oneday", "Test","T20"];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("ListviewDemo"),
        centerTitle: true,
      ),
      body: ListView.separated(
        itemCount: playerinfo.length,
        separatorBuilder: (BuildContext, int index) {
          return Text(cricformat[index]);
        },
        // ListView.builder(
        // itemCount: playerinfo.length,
        itemBuilder: (BuildContext context, int outerIndex) {
          return Column(
            children: [
              for (int innerIndex = 0;
                  innerIndex < playerinfo[outerIndex].length;
                  innerIndex++)
                Container(
                  height: 200,
                  width: 200,
                  margin: const EdgeInsets.all(10),
                  child: Image.network(playerinfo[outerIndex][innerIndex]),
                ),
            ],
          );
        },
      ),
    );
  }
}
